import{_ as l,c as n,o,j as e}from"./chunks/framework.BHpayLOB.js";const m=JSON.parse('{"title":"Мы не заслужили успех","description":"","frontmatter":{"title":"Мы не заслужили успех","tags":["#творчество"]},"headers":[],"relativePath":"notes/note-2023-01-20.md","filePath":"notes/note-2023-01-20.md"}'),a={name:"notes/note-2023-01-20.md"};function s(p,t,r,u,d,i){return o(),n("div",null,t[0]||(t[0]=[e("p",null,'Вчера заставил себя посмотреть документальный фильм "RADIOHEAD: A Job That Slowly Kills You". Сложный, но красивый. Несколько раз перематывал вперед, но возвращался и пересматривал. К концу досмотрел всё и был вознагражден за внимательность.',-1),e("p",null,'Итак, документальный фильм с любовью (по другому не скажешь) создан фанатом Radiohead. Эстетически это погружение в мир Radiohead и особенно в ту его часть, которая меня всегда интересовала: как можно было создать "OK Computer" - один из лучших альбомов 20 века (не я один так думаю), который в 1997 году изменил мой музыкальный вкус навсегда?',-1),e("p",null,'И тут кстати цитата из интервью в фильме: Журналист: "После релиза ОК Computer к вам быстро пришла мировая слава. Вам было 22-23 года, очень чувствительный возраст. Как вы справились со всеми изменениями, которые с вами произошли?"',-1),e("p",null,'Том Йорк (солист): "Я чувствовал панику. Нам казалось, что мы абсолютно этого не заслужили. Последние 10 лет мы делали лучшее, что мы могли сделать, и в один момент двери открылись, но мы были не готовы. Я не жалуюсь конечно. Без этого у нас не было бы шанса провести месяцы для записи следующего альбома (KID A) в студии. Так работали звукозаписывающие компании тогда. Тебе не позволяли делать то, что ты хочешь. Только через несколько лет после этого мы поняли, что мы интересны нашей аудитории за то, какими мы были на самом деле. Казалось, что наши возможности безграничны. Мы чувствовали эту поддержку и боролись так сильно, как могли, чтобы получить это. Проблема в том, что тогда я не испытал от этого удовольствия. Я боялся отпустить руль, я был очень сконцентрирован на процессе и боялся допустить малейшую ошибку".',-1),e("p",null,'Тут сразу несколько интересных мыслей, но больше всего обратила на себя внимание эта: "... мы интересны нашей аудитории за то, какими мы были на самом деле".',-1),e("p",null,'Мой творческий прорыв в дизайне случился несколько лет назад, когда я допустил идею, что могу себе позволить отпустить внутреннего цензора и просто делать вещи такими, как я их вижу. Я был точен, сконцентрирован и одновременно расслаблен. В итоге получил доступ к таким способам взаимодействия с творческим миром, которые раньше были для меня не доступны. Так появился "мой" стиль и одновременно с ним уверенность в своих силах.',-1),e("p",null,"К слову, всё это почти не имеет отношения к бизнес-проектам. Там всё проще, но подобный опыт безусловно приносит хороший результат и там.",-1),e("p",null,"Теперь, пройдя условный творческий круг, я решил вернуться к тому, с чего всё началось - к айдентике для Фонда Хворостовского. Так совпало, что мы создаем новый сайт для фонда и он потребовал нового прочтения истории, которая казалось для меня уже закрытой.",-1),e("p",null,"Иногда интересную работу нужно придумать.",-1)]))}const _=l(a,[["render",s]]);export{m as __pageData,_ as default};
