import{_ as e,c as o,o as a,ag as n}from"./chunks/framework.BHpayLOB.js";const l=JSON.parse('{"title":"Гениальная простота - Лекция","description":"","frontmatter":{"title":"Гениальная простота - Лекция"},"headers":[],"relativePath":"notes/note-2023-05-27.md","filePath":"notes/note-2023-05-27.md"}'),p={name:"notes/note-2023-05-27.md"};function r(s,t,_,i,c,u){return a(),o("div",null,t[0]||(t[0]=[n('<h1 id="гениальная-простота" tabindex="-1">Гениальная простота <a class="header-anchor" href="#гениальная-простота" aria-label="Permalink to &quot;Гениальная простота&quot;">​</a></h1><p>Гениальная простота по сути означает ясный подход к решению какой-то проблемы. В моём случае я помогаю людям творческих профессий одним простым и в тоже время комплексным решением - системой творческой продуктивности.</p><p>В основе гениальной простоты есть один, но очень действенный подход, о котором говорил Микеланджело Буонарроти и Альберт Эйнштейн - убирать всё лишнее.</p><p>Моя книга &quot;Творите, что хотите&quot; началась несколько месяцев назад с заметки &quot;Дайте себе разрешение на творчество&quot;. Потом появилось ещё несколько заметок, но плана на книгу не было до того, как я перевел на русский и сделал саммари книги Рика Рубина &quot;The Creative Act: A Way of being&quot;.</p><p>Самый простой способ узнать, что вы хотите - это узнать то, чего вы НЕ хотите. Я не хотел писать о том, о чем уже написал Рик. Во-первых, я сделал подробное саммари и любой теперь может почитать его на моем сайте, а во-вторых, меня больше интересует не природа творчества, а как сделать больше людей вокруг творческими, т.е. открытыми, наполненными, ясными, людьми, которые стремятся прожить человеческую жизнь от и до.</p><p>Когда мой фокус ограничился творческой продуктивностью, то стало очевидно, что нужно описать систему знаний и действий, которую могут использовать другие люди, а не только я. Эта амбициозная задача потребовала месяца сложной работы, когда мне пришлось объединить всю систему знаний, которую я накопил, и подходы, которые я использую. Результатом стала статья на vc.ru.</p><p>Несмотря на название, до простоты и тем более гениальности, было ещё далеко и идея записать лекцию, показалась решением в нужном направлении.</p><p>В итоге, ещё месяц потребовался на переосмысление того, что было сделано, подготовку презентации, дизайн слайдов, запись и монтаж видео.</p><p>Спустя 3 месяца я прошел путь от идеи написать книгу до системы творческой продуктивности &quot;Гениальная простота&quot; в формате 40 мин. лекции, которую вы можете посмотреть, прочитать, пролистать и поделиться с другими увлеченными людьми, как я.</p>',9)]))}const f=e(p,[["render",r]]);export{l as __pageData,f as default};
