import{_ as a,c as n,o,j as e,a as l}from"./chunks/framework.BHpayLOB.js";const f=JSON.parse('{"title":"Человек на 15%","description":"","frontmatter":{"title":"Человек на 15%"},"headers":[],"relativePath":"notes/note-2024-10-26.md","filePath":"notes/note-2024-10-26.md"}'),r={name:"notes/note-2024-10-26.md"};function s(p,t,c,d,i,u){return o(),n("div",null,t[0]||(t[0]=[e("h1",{id:"человек-на-15",tabindex:"-1"},[l("Человек на 15% "),e("a",{class:"header-anchor",href:"#человек-на-15","aria-label":'Permalink to "Человек на 15%"'},"​")],-1),e("p",null,"В 1979 году в Токио Святослав Рихтер исполнил мечтательную и в то же время яркую Прелюдию № 4 в ре мажоре Сергея Рахманинова. Это исполнение высоко ценится за интерпретацию, выразительность, тонкость и эмоциональную насыщенность. Рихтер неоднократно возвращался к этому произведению, включая его в свои программы в разное время.",-1),e("p",null,"Прелюдия № 4 была впервые исполнена 10 февраля 1903 года в Москве. Она быстро стала одной из самых исполняемых пьес из всего цикла опуса 23 во многом благодаря её связи с русским национальным стилем, характерным для Рахманинова.",-1),e("p",null,"Несколько лет я занимался биографией Скрябина. Люблю через музыку разобраться, что происходило с человеком в то или иное время его жизни. Следом я переключился на Рахманинова. Симфония №2 помогла мне пережить выход из пост-травмы, и она же стала входным билетом в мир Рахманинова. Полгода я был непрерывно занят работой с ИИ. Это было трудно и важно было найти такую точку синхронизации, где ум работает максимально точно и в тоже время широко. В итоге не заметил как эта музыка стала частью моего образа мышления. Буквально ловишь себя «на мысли», как конструкции строятся патернами Рахманинова. Музыканты знают как это.",-1),e("p",null,"Вообще в моей коллекции сейчас около 30-40 биографий писателей, художников, музыкантов и миценатов. Люблю находить что-то особенное в людях, то что делает их уникальными. Как-то раз я сделал подробный разбор, почему эти люди вызывают мой интерес. Так вышло, что их объединяет время конец 19 – начало 20 века и одна общая черта – широта души.",-1),e("p",null,"Примечательно, как однажды Сергей Рахманинов сказал, что считает себя на 85% музыкантом и «немножко человеком» на оставшиеся 15%. Некоторые считают, что на самом деле он был не на 15%, а на 150% человеком, учитывая его поступки и отношение к окружающим.",-1),e("p",null,"Сегодня я сформулировал задачу в своём дневнике так: «Быть Человеком на 15%». Кажется, этого должно быть достаточно.",-1)]))}const _=a(r,[["render",s]]);export{f as __pageData,_ as default};
