import{_ as n,c as l,o,j as e}from"./chunks/framework.BHpayLOB.js";const m=JSON.parse('{"title":"Достичь большого успеха в чем-либо можно только через отчаяние","description":"","frontmatter":{"title":"Достичь большого успеха в чем-либо можно только через отчаяние"},"headers":[],"relativePath":"notes/note-2024-12-08.md","filePath":"notes/note-2024-12-08.md"}'),a={name:"notes/note-2024-12-08.md"};function s(r,t,p,u,c,d){return o(),l("div",null,t[0]||(t[0]=[e("p",null,"Достичь большого успеха в чем-либо можно только через отчаяние.",-1),e("p",null,"Поговаривают, что определённая форма борьбы абсолютно необходима для того, чтобы привести нас к той глубине, из которой возникает креативность. Вероятно под глубиной понимается именно та его часть, которая - Дно. Но только такая креативность, которую мы не принимаем. Неправильная креативность. Такая звенящая, что и не креативность вовсе. \u2028Год назад я стартовал новую компанию по ИИ-автоматизации бизнеса – Модуль Роста. Мне показалось, что будет правильно и даже честно полностью отказался от творческих, креативных и иных проектов, которые кормили меня и приносили радость в жизни, чтобы впервые сделать что-то большее, чем я сам. Глупость конечно, но как есть.\u2028 Я принял решение потратить всё, что у меня есть и ещё больше, чтобы выстрелить как пуля. Так в сути и вышло, за исключением того, что выстрел случился в стиле Вронского из Анны Карениной, т.е. в себя и с последствиями.",-1),e("p",null,"Бывает так, что на 41 году жизни понимаешь, что саму себе можно быть не только другом, но и врагом и это даже лучше, в случае, когда предпринятые меры не приносят нужного результата. В такой момент приходит на помощь тот, кто может действительно что-то изменить и это уже не ты.",-1),e("p",null,"Этот кто-то берёт что-то, точное и острое, назовём, секатор и внимательно, методично убирает всё лишнее таким образом, чтобы 1) оно уже не прорасло заново 2) чтобы уже начало расти как надо и давало плоды. Какие плоды? Самые обычные. Человеческие.",-1),e("p",null,"В общем и целом, мои попытки что-то предпринять привели к отчаянью. Именно там удалось хорошенько разглядеть всё, что нужно. Дальше был большой и долгий путь наверх и в стороны, и вот, сегодня (будем считать, что сегодня) я делаю новую попытку сделать что-то большее, чем я сам и вовлечь в это как можно больше людей.",-1),e("p",null,"Не осталось уже ничего, кроме пустоты, которая требует, чтобы её наполнили всем, что может и должно расти, знает об этом и действует.",-1),e("p",null,null,-1)]))}const _=n(a,[["render",s]]);export{m as __pageData,_ as default};
