import{_ as a,c as t,o as _,ag as r}from"./chunks/framework.BHpayLOB.js";const h=JSON.parse('{"title":"2.1 Я как контекст (self-as-context)","description":"","frontmatter":{"title":"2.1 Я как контекст (self-as-context)"},"headers":[],"relativePath":"projects/diary-guide/keys/2/2.1 Я как контекст.md","filePath":"projects/diary-guide/keys/2/2.1 Я как контекст.md"}'),i={name:"projects/diary-guide/keys/2/2.1 Я как контекст.md"};function o(s,e,l,n,d,c){return _(),t("div",null,e[0]||(e[0]=[r('<h2 id="воспринимаите-себя-как-другого-человека" tabindex="-1">Воспринимайте себя, как другого человека <a class="header-anchor" href="#воспринимаите-себя-как-другого-человека" aria-label="Permalink to &quot;Воспринимайте себя, как другого человека&quot;">​</a></h2><ol><li>Если не ассоциироваться с мыслями и историями о себе, а смотреть на них со стороны, как наблюдатель, открывается новое пространство для мысли.</li><li>Когда вы описываете ситуацию, называйте себя в третьем лице, если хотите, чтобы ум обратил на внимание на то, что вам важно сейчас и позвольте ему сделать всю работу.</li><li>Мозг не воспринимает чужие проблемы как свои и не включает центры негативных эмоций, и благодаря этому остается больше энергии на внимание и в итоге принятие правильных решений</li><li>Если делаете выводы, то обращайтесь к себе во втором лице с глаголами в повелительном наклонении: «Сходи на тренировку. Съешь порцию фруктов. Выпей воды». Формулировки, имитирующие чужие команды, советы или просьбы, лучше настраивают на работу и внушают больше уверенности в себе. Такие формулировки переключают мозг на режим постановки задач.</li></ol><p>Если так делать какое-то время, то в итоге ведение дневника разделится на 2 процесса: рефлексия и действия.</p><hr><h2 id="содержание" tabindex="-1">[[Содержание]] <a class="header-anchor" href="#содержание" aria-label="Permalink to &quot;[[Содержание]]&quot;">​</a></h2>',5)]))}const f=a(i,[["render",o]]);export{h as __pageData,f as default};
