import{_ as a,c as e,o as t,ag as o}from"./chunks/framework.BHpayLOB.js";const u=JSON.parse('{"title":"2.6 Задавайте вопросы и слушайте ответы","description":"","frontmatter":{"title":"2.6 Задавайте вопросы и слушайте ответы"},"headers":[],"relativePath":"projects/diary-guide/keys/2/2.6 Задавайте вопросы и слушайте ответы.md","filePath":"projects/diary-guide/keys/2/2.6 Задавайте вопросы и слушайте ответы.md"}'),r={name:"projects/diary-guide/keys/2/2.6 Задавайте вопросы и слушайте ответы.md"};function s(i,_,p,n,d,c){return t(),e("div",null,_[0]||(_[0]=[o('<h2 id="в-вопросе-есть-уже-половина-ответа" tabindex="-1">В вопросе есть уже половина ответа <a class="header-anchor" href="#в-вопросе-есть-уже-половина-ответа" aria-label="Permalink to &quot;В вопросе есть уже половина ответа&quot;">​</a></h2><p>Научитесь спрашивать, как будто ваше второе я уже всё знает и нужно только правильно спросить.</p><p>Задавать вопросы можно как себе, так и другим. Если вы хотите подсветить какие-то свои нерешенные вопросы, вы можете просто рассказать какой-то отрывок кому-то другому входе обычного разговора и спросить, чтоб он об этом думает. Сам ответ будет не важен.</p><p>Ловите ваши мысли, которые возникают, когда вы слышите не то, что ожидали. Обязательно зафиксируйте это сразу после любым удобным способом, ничего не прибавляя и не обдумывая. Предоставьте подсознанию сделать за вас всю работу.</p><h3 id="неудобные-вопросы" tabindex="-1">Неудобные вопросы <a class="header-anchor" href="#неудобные-вопросы" aria-label="Permalink to &quot;Неудобные вопросы&quot;">​</a></h3><p>Задавайте вопросы, которые ставят в тупик: &quot;Неудобные&quot; вопросы и/или &quot;А что, если&quot; вопросы.</p><p>&quot;Неудобные&quot; вопросы - это все вопросы, которых мы избегаем и прячем под маской размышлений. Позвольте себе быть честным на миг или два. Не копайте глубоко. Просто спросите и пройдите дальше.</p><p>&quot;А что, если&quot; вопросы предполагают в себе идею, сомнение и свободу одновременно. По моей статистике именно такие вопросы чаще всего становятся задачами, которые реализуются на практике.</p><p><strong>Обращайте внимание на то, как мозг подстраивается под вас и начинает думать в формате вопрос-ответ.</strong></p><p>Сделайте практику ставить и отвечать на вопросы частью еженедельных ритуалов.</p><hr><h2 id="содержание" tabindex="-1">[[Содержание]] <a class="header-anchor" href="#содержание" aria-label="Permalink to &quot;[[Содержание]]&quot;">​</a></h2>',12)]))}const h=a(r,[["render",s]]);export{u as __pageData,h as default};
