import{_ as a,c as i,o as t,ag as r}from"./chunks/framework.BHpayLOB.js";const b=JSON.parse('{"title":"Саммари на русском The Creative Act - A Way of Being - Rick Rubin","description":"","frontmatter":{"title":"Саммари на русском The Creative Act - A Way of Being - Rick Rubin","draft":null,"link":"summary-the-creative-act-rick-rubin"},"headers":[],"relativePath":"projects/the-creative-act-rick-rubin/Саммари The Creative Act - A Way of Being.md","filePath":"projects/the-creative-act-rick-rubin/Саммари The Creative Act - A Way of Being.md"}'),o={name:"projects/the-creative-act-rick-rubin/Саммари The Creative Act - A Way of Being.md"};function l(n,e,h,c,s,d){return t(),i("div",null,e[0]||(e[0]=[r('<h1 id="саммари-the-creative-act-a-way-of-being-rick-rubin" tabindex="-1">Саммари - The Creative Act: A Way of Being, [[Rick Rubin]] <a class="header-anchor" href="#саммари-the-creative-act-a-way-of-being-rick-rubin" aria-label="Permalink to &quot;Саммари - The Creative Act: A Way of Being,  [[Rick Rubin]]&quot;">​</a></h1><h1 id="содержание" tabindex="-1">Содержание <a class="header-anchor" href="#содержание" aria-label="Permalink to &quot;Содержание&quot;">​</a></h1><p>[[1. Природа творчества]] [[2. Зачем заниматься творчеством]] [[3. Творчество - советы и практики]] [[4. Вдохновение]] [[5. Творческий процесс]] [[6. Что может помешать творчеству]] [[7. Совместная работа]] [[Заключение]]</p><hr><h2 id="сегодня-17-марта-2023-года-и-книга-рика-рубина-the-creative-act-a-way-of-being-rick-rubin-the-creative-act-a-way-of-being-спустя-2-месяца-после-публикации-уже-бестселлер-no1-new-york-times" tabindex="-1">Сегодня 17 марта 2023 года и книга Рика Рубина [[The Creative Act - A Way of Being, Rick Rubin|The Creative Act - A Way of Being,]] спустя 2 месяца после публикации уже <strong><em>Бестселлер №1 New York Times</em></strong>. <a class="header-anchor" href="#сегодня-17-марта-2023-года-и-книга-рика-рубина-the-creative-act-a-way-of-being-rick-rubin-the-creative-act-a-way-of-being-спустя-2-месяца-после-публикации-уже-бестселлер-no1-new-york-times" aria-label="Permalink to &quot;Сегодня 17 марта 2023 года и книга Рика Рубина [[The Creative Act - A Way of Being, Rick Rubin|The Creative Act - A Way of Being,]] спустя 2 месяца после публикации уже **_Бестселлер №1 New York Times_**.&quot;">​</a></h2><blockquote><p><strong>Моё первое впечатление о книге - великолепное и вдохновляющее произведение о природе творчества. Она безусловно порадует сердца творческих профессионалов и заставит их биться с новым смыслом и направлением.</strong></p></blockquote><h2 id="идея-этого-саммари" tabindex="-1">Идея этого саммари <a class="header-anchor" href="#идея-этого-саммари" aria-label="Permalink to &quot;Идея этого саммари&quot;">​</a></h2><ol><li>Мой творческий опыт фактически начался в 1998 году. С того момента я сделал тысячи творческих проектов и воплотил все свои желания в реальность, но до 2020 года я считал себя не вполне творческим человеком в сравнении с теми, кто был для меня значим.</li><li>Чтобы понять, что это не так, мне пришлось освоить 12 разных творческих профессий и в один чудесный момент дать себе разрешение на творчество. Трудно поверить в отсутствие у меня творческой искры, но та свобода и уверенность, которые у меня появились позже позволили, имея на руках те же карты, начать совсем другую игру.</li><li>Именно такое стечение обстоятельств позволяет мне теперь не просто работать с творческими профессионалами, а с уверенностью полагаться на их мнение и говорить с ними на одном языке.</li><li>Я прочитал книгу Рика Рубина почти сразу после публикации и она оказалась так хороша, что мне захотелось перевести её и поделиться авторским резюме с такими же увлеченными людьми, как я. Несмотря на то, что книг о творчестве очень много, именно в этой книге даны простые и точные советы, которые будут полезны всем творческим профессионалам.</li></ol><p><strong>Это саммари удобно тем, что к нему можно возвращаться и перечитывать нужные части. Например, если у вас возник творческий тупик или чувствуете неуверенность, вы всегда можете найти здесь подсказки, попробовать что-то сделать и вернуться, чтобы прочитать это снова уже новым взглядом. Это очень удобно и продуктивно.</strong></p><h2 id="что-такое-саммари" tabindex="-1">Что такое саммари <a class="header-anchor" href="#что-такое-саммари" aria-label="Permalink to &quot;Что такое саммари&quot;">​</a></h2><ol><li>Саммари — это сжатое изложение материала либо обобщение информации, которая представлена в оригинальном источнике.</li><li>Вы можете найти хорошие саммари в <a href="https://smartreading.ru/" target="_blank" rel="noreferrer">Smart Reading</a>, но книги Рика Рубина там нет и поэтому мне пришлось сделать своё резюме и заодно перевести её на русский..</li></ol><h2 id="мои-впечатления" tabindex="-1">Мои впечатления <a class="header-anchor" href="#мои-впечатления" aria-label="Permalink to &quot;Мои впечатления&quot;">​</a></h2><ol><li>Всё, что пишет Рик мне очень хорошо знакомо. Все темы творческого процесса раскрыты подробно. Просто сказка.</li><li>Когда начал готовить своё резюме, то оказалось, что по-настоящему полезного текста, т.е. такого текста, которым можно вооружиться и применить с пользой для себя, в книге не более 10%. Именно поэтому я сделал саммари.</li><li>Переводя текст и делая резюме, я всё чаще не соглашался с Риком, когда речь заходила об ответственности перед обществом или о продуктивности. Либо я перевожу книгу, как есть, либо я пишу свои мысли, но тогда это - не перевод. В итоге остались только идеи Рика.</li></ol><h2 id="отличия-этого-саммари-от-книги-рика" tabindex="-1">Отличия этого саммари от книги Рика <a class="header-anchor" href="#отличия-этого-саммари-от-книги-рика" aria-label="Permalink to &quot;Отличия этого саммари от книги Рика&quot;">​</a></h2><ol><li><strong>Подача материала.</strong> Книга Рика написана особым творческим языком. Я не имею ввиду английский язык, а тот способ изложения, который, как мне кажется, не позволяет сделать адаптированный русский перевод. Это саммари написано так, чтобы его можно было быстро прочитать и усвоить нужную информацию. В оригинале так сделать конечно не получится. Совсе другое ощущение.</li><li><strong>Artist.</strong> Главный герой книги - artist, т.е. любой человек творческой профессии, а не буквально художник, как могло бы следовать из перевода. Поэтому стоит задача придумать название главного героя так, чтобы это было коротко, ясно, и главное - передавало смысл, звучало на русском. Вы можете встретить разные наименования в этом саммари, но это скоро поправится.</li><li><strong>Главы.</strong> В книге Рика - 78 глав. Каждая из них последовательно рассказывает историю того или иного аспекта творческой деятельности. В этом саммари - 8 глав вместе с моим заключением. Когда началась работа над текстом, я понял, что все выводы, которые я сделал по каждой из 78 глав, лучше всего объединить в новые главы, добавить к ним ту информацию, которая у меня уже накопилась так, чтобы это стало по-настоящему полезным материалом именно для творческих профессионалов, т.е. для людей которые знают о творчестве и живут им каждый день.</li></ol><hr><h2 id="правовые-вопросы" tabindex="-1">Правовые вопросы <a class="header-anchor" href="#правовые-вопросы" aria-label="Permalink to &quot;Правовые вопросы&quot;">​</a></h2><h3 id="самостоятельное-произведение" tabindex="-1">Самостоятельное произведение <a class="header-anchor" href="#самостоятельное-произведение" aria-label="Permalink to &quot;Самостоятельное произведение&quot;">​</a></h3><ol><li>Это саммари является самостоятельным произведением, где заимствованы только неохраняемые элементы исходного произведения - книги Рика Рубина &quot;The Creative Act&quot; A Way of Being&quot;. Cогласие автора и правообладателя не требуется. <a href="https://kad.arbitr.ru/Card/1eeffd96-d1ad-4c21-b939-689936e4c29f" target="_blank" rel="noreferrer">Дело № А40-48760/16</a>. Автор саммари - Михаил Изюмов.</li><li>На момент публикации 14.04.2023 книга существует только на английском языке. Перевода книги на русский язык нет. В связи с этим в этом саммари не используется цитирование, тексты саммари и оригинала не совпадают. Проведена соответствующая проверка.</li><li>В саммари использованы исключительно идеи из произведения, без заимствования системы образов. Содержатся авторские ремарки.</li><li>Источником саммари является перевод книги с оригинала на русский язык Михаилом Изюмовым в личных образовательных целях.</li><li>Это саммари публикуется в образовательных целях и не преследует цели извлечения дохода.</li></ol><h3 id="на-самом-деле" tabindex="-1">На самом деле <a class="header-anchor" href="#на-самом-деле" aria-label="Permalink to &quot;На самом деле&quot;">​</a></h3><ol><li>Я постарался сохранить суть книги, как я её понял с учетом моего знания языка и творческого опыта. Моя главная задача - не потерять смысл, который хотел передать Рик.</li><li>Из-за особенностей подачи материала Риком, а также из-за того, что я хотел сделать именно моё авторское видение резюме этой книги, от оригинала ничего не осталось. Получилась скорее призма, видение, о чем как раз и пишет Рик.</li><li>Я безусловно советую купить и прочитать книгу Рика Рубина [[The Creative Act - A Way of Being, Rick Rubin|The Creative Act - A Way of Being,]] на английском в оригинале так, как это сделал я на Amazon.</li></ol><h2 id="для-внимательного-читателя" tabindex="-1">Для внимательного читателя <a class="header-anchor" href="#для-внимательного-читателя" aria-label="Permalink to &quot;Для внимательного читателя&quot;">​</a></h2><p>Вы можете обратить внимание, что название книги осталось без перевода. Я делаю это сознательно, т.к. до настоящего момента не смог придумать подходящее адаптированное название и сделаю это позже. Если вы думаете, что можете справиться с этой задаче, то взгляните на [[Варианты перевода заголовка The Creative Act|эту страницу]] и присоединяйтесь к обсуждению в моем <a href="https://t.me/izumov" target="_blank" rel="noreferrer">Телеграм-канале</a>.</p><hr><h1 id="содержание-саммари" tabindex="-1">Содержание саммари <a class="header-anchor" href="#содержание-саммари" aria-label="Permalink to &quot;Содержание саммари&quot;">​</a></h1><p>[[Саммари The Creative Act - A Way of Being|Вступление]] [[1. Природа творчества]] [[2. Зачем заниматься творчеством]] [[3. Творчество - советы и практики]] [[4. Вдохновение]] [[5. Творческий процесс]] [[6. Что может помешать творчеству]] [[7. Совместная работа]] [[Заключение]]</p>',26)]))}const f=a(o,[["render",l]]);export{b as __pageData,f as default};
